mapper.select:
		
		select * from
		(select no, memberid, title, content, regdate, readcount, recommend, cate, rownum r
		from (select * from communityboard order by regdate desc)
		order by regdate desc)where r >= #{start} AND r <= #{end}

select * from (select no, memberid,title, content, regdate, readcount, recommend, cate, rownum r from (select * from communityboard order by regdate desc)
title like '%'||#{search}||'%' content like '%'||#{search}||'%' memberid like '%'||#{search}||'%' title like '%'||#{search}||'%' or content like '%'||#{search}||'%'
order by regdate desc) where r >= #{start} AND r <= #{end}

		
		select * from
		(select no, memberid, title, content, regdate, readcount, recommend, cate, rownum r
		from (select * from communityboard where recommend >= 3 order by regdate desc)
		order by regdate desc)where r >= #{start} AND r <= #{end}

select * from (select no, memberid,title, content, regdate, readcount, recommend, cate, rownum r from (select * from communityboard where recommend >= 3 order by regdate desc)
title like '%'||#{search}||'%' content like '%'||#{search}||'%' memberid like '%'||#{search}||'%' title like '%'||#{search}||'%' or content like '%'||#{search}||'%'
order by regdate desc) where r >= #{start} AND r <= #{end}

		
		select * from
		(select no, memberid, title, content, regdate, readcount, recommend, cate, rownum r
		from (select * from communityboard where cate=#{cate} order by regdate desc)
		order by regdate desc)where r >= #{start} AND r <= #{end}

select * from (select no, memberid,title, content, regdate, readcount, recommend, cate, rownum r from (select * from communityboard where cate=#{cate} order by regdate desc)
title like '%'||#{search}||'%' content like '%'||#{search}||'%' memberid like '%'||#{search}||'%' title like '%'||#{search}||'%' or content like '%'||#{search}||'%'
order by regdate desc) where r >= #{start} AND r <= #{end}
